// import * as React from 'react';
import React, { useState, useEffect } from "react";
import { StyleSheet, Image, ImageBackground, Button } from "react-native";
import { fetchUser } from "../redux/actions/index";

import EditScreenInfo from "../Components/EditScreenInfo";
import { Text, View } from "../Components/Themed";

import firebase from "firebase";
require("firebase/firestore");
import { connect } from "react-redux";
import { user } from "../redux/reducer/user";

function DetailsScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Details Screen</Text>
      <Button
        title="Go to Details... again"
        onPress={() => navigation.push('Details')}
      />
      <Button title="Go to Home" onPress={() => navigation.navigate('Notificatio')} />
      <Button title="Go back" onPress={() => navigation.goBack()} />
      <Button
        title="Go back to first screen in stack"
        onPress={() => navigation.popToTop()}
      />
    </View>
  );
}

function ProfileScreen(props, { navigation }) {
  const { currentUser } = props;
  console.log({ currentUser });

  return (
    <ImageBackground
      source={require("../assets/background2.png")}
      style={styles.container}
    >
      {/* <Text style={styles.containerInfo}>{currentUser.name}</Text>
      <Text style={styles.containerInfo}>{currentUser.email}</Text> */}
      <Text style={styles.box}>Name: {currentUser.name} </Text>
      <Text style={styles.box}>Email: {currentUser.email} </Text>
      <Text style={styles.box}>Bio: {currentUser.bio} </Text>
    </ImageBackground>

    // <View style={styles.container}>
    //   <Text style={styles.title}>Profile</Text>
    //   <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
    //   {/* <EditScreenInfo path="/screens/ProfileScreen.tsx" /> */}
    // </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  containerInfo: {
    marginTop: 10,
  },
  box: {
    backgroundColor: "#ffb4b0",
    shadowOpacity: 0.25,
    borderRadius: 10,
    paddingVertical: 10,

    width: 300,
    height: 70,
    marginTop: 40,
    alignSelf: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
});

const mapStateToProps = (store) => ({
  currentUser: store.userState.currentUser,
});
export default connect(mapStateToProps, null)(DetailsScreen);
